BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "backup_config" (
	"id"	INTEGER CHECK("id" = 1),
	"enabled"	BOOLEAN NOT NULL DEFAULT 0,
	"interval_value"	INTEGER NOT NULL DEFAULT 24,
	"interval_unit"	TEXT NOT NULL DEFAULT 'hours',
	"last_backup"	TEXT,
	"updated_at"	TEXT DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "bitacora_acciones" (
	"id"	INTEGER,
	"usuario_id"	INT NOT NULL,
	"accion"	VARCHAR(255) NOT NULL,
	"detalles"	TEXT,
	"fecha"	DATETIME DEFAULT CURRENT_DATETIME,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("usuario_id") REFERENCES "usuarios"("id")
);
CREATE TABLE IF NOT EXISTS "categorias" (
	"id_categoria"	INTEGER,
	"nombre_categoria"	varchar(100) NOT NULL UNIQUE,
	"descripcion"	TEXT,
	"fecha_creacion"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	"fecha_actualizacion"	TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY("id_categoria" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "error_reports" (
	"id"	INTEGER,
	"usuario_id"	INT NOT NULL,
	"modulo"	VARCHAR(100) NOT NULL,
	"accion"	TEXT NOT NULL,
	"descripcion"	TEXT NOT NULL,
	"estado"	VARCHAR(20) DEFAULT 'pendiente',
	"fecha_reporte"	DATETIME DEFAULT CURRENT_TIMESTAMP,
	"fecha_resuelto"	DATETIME,
	"notas_resolucion"	TEXT,
	"tipo"	VARCHAR(20) DEFAULT 'manual',
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("usuario_id") REFERENCES "usuarios"("id")
);
CREATE TABLE IF NOT EXISTS "factura_detalles" (
	"id"	INTEGER,
	"factura_id"	INT NOT NULL,
	"producto_id"	INT NOT NULL,
	"cantidad"	INT NOT NULL,
	"precio_unitario_usd"	REAL NOT NULL,
	"subtotal_usd"	REAL NOT NULL,
	"nombre_producto"	TEXT,
	"descripcion_producto"	TEXT,
	"categoria_producto"	TEXT,
	"barcode_producto"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("factura_id") REFERENCES "facturas"("id"),
	FOREIGN KEY("producto_id") REFERENCES "productos"("id")
);
CREATE TABLE IF NOT EXISTS "facturas" (
	"id"	INTEGER,
	"usuario_id"	INT NOT NULL,
	"tasa_id"	INT NOT NULL,
	"total_usd"	REAL NOT NULL,
	"total_ves"	REAL NOT NULL,
	"fecha_factura"	DATETIME DEFAULT CURRENT_TIMESTAMP,
	"estado"	VARCHAR(20) NOT NULL DEFAULT 'Completada',
	"ticket_impreso"	BOOLEAN DEFAULT 0,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("tasa_id") REFERENCES "tasas_de_cambio"("id"),
	FOREIGN KEY("usuario_id") REFERENCES "usuarios"("id")
);
CREATE TABLE IF NOT EXISTS "permissions" (
	"id"	INTEGER,
	"name"	TEXT NOT NULL UNIQUE,
	"description"	TEXT,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "preguntas_seguridad" (
	"id"	INTEGER,
	"pregunta"	TEXT NOT NULL UNIQUE,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "productos" (
	"id"	INTEGER,
	"nombre"	VARCHAR(150) NOT NULL,
	"descripcion"	TEXT,
	"precio_costo"	REAL NOT NULL,
	"precio_venta"	REAL NOT NULL,
	"categoria"	VARCHAR(100),
	"image_url"	TEXT,
	"fecha_creacion"	DATETIME DEFAULT CURRENT_DATETIME,
	"fecha_actualizacion"	DATETIME DEFAULT CURRENT_DATETIME,
	"barcode"	VARCHAR(255) UNIQUE,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "respuestas_seguridad_usuario" (
	"id"	INTEGER,
	"usuario_id"	INTEGER NOT NULL,
	"pregunta_id"	INTEGER NOT NULL,
	"respuesta"	TEXT NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("pregunta_id") REFERENCES "preguntas_seguridad"("id"),
	FOREIGN KEY("usuario_id") REFERENCES "usuarios"("id") ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "rol_permisos" (
	"rol_id"	INTEGER NOT NULL,
	"permission_id"	INTEGER NOT NULL,
	PRIMARY KEY("rol_id","permission_id"),
	FOREIGN KEY("permission_id") REFERENCES "permissions"("id") ON DELETE CASCADE,
	FOREIGN KEY("rol_id") REFERENCES "roles"("id") ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "roles" (
	"id"	INTEGER,
	"nombre"	VARCHAR(50) NOT NULL UNIQUE,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "tasas_de_cambio" (
	"id"	INTEGER,
	"tasa"	REAL NOT NULL,
	"fecha"	DATE NOT NULL,
	"fecha_hora"	DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "usuarios" (
	"id"	INTEGER,
	"rol_id"	INT NOT NULL,
	"nombre_completo"	VARCHAR(100) NOT NULL,
	"cedula"	VARCHAR(11) NOT NULL UNIQUE,
	"contrasena"	VARCHAR(50) NOT NULL,
	"fecha_creacion"	DATETIME DEFAULT CURRENT_DATETIME,
	"fecha_actualizacion"	DATETIME DEFAULT CURRENT_DATETIME,
	"telefono"	VARCHAR(11) NOT NULL UNIQUE,
	"nombre_usuario"	VARCHAR(50) NOT NULL,
	PRIMARY KEY("id" AUTOINCREMENT),
	FOREIGN KEY("rol_id") REFERENCES "roles"("id")
);
CREATE INDEX IF NOT EXISTS "idx_tasas_fecha_hora" ON "tasas_de_cambio" (
	"fecha_hora"	DESC
);
COMMIT;
